import {Component, OnInit} from '@angular/core';
import {ModalComponent} from "../modal/modal.component";
import {NgbModal} from "@ng-bootstrap/ng-bootstrap";
import {TransactionService} from "../../services/transaction.service";
import {DecimalPipe} from "@angular/common";

@Component({
  selector: 'app-transfer-form',
  templateUrl: './transfer-form.component.html',
  styleUrls: ['./transfer-form.component.scss'],
  providers: [TransactionService, DecimalPipe]
})
export class TransferFormComponent implements OnInit {
  public accountName: string;
  public balance: number;
  public amount: number;
  public toAccount: string;

  constructor(private modalService: NgbModal, private transactionService: TransactionService) {
    this.accountName = 'Free checking(4692)';
    this.balance = 5824.76;
    this.toAccount = '';

  }

  open() {
    const modalRef = this.modalService.open(ModalComponent);
    modalRef.componentInstance.accountName = this.accountName;
    modalRef.componentInstance.toAccount = this.toAccount;
    modalRef.componentInstance.amount = this.amount;

    modalRef.result.then(()=>{
      this.submit()
    }, ()=>{
      this.cancel()
    });
  }

  submit() {

    this.transactionService.add({
      amount: this.amount,
      categoryCode: "#e25a2c",
      merchant: this.toAccount,
      merchantLogo: 'data:image/jpeg;base64,',
      transactionDate: new Date().getTime(),
      transactionType: 'Transaction'
    });
    this.balance -= this.amount;
    // clear form
    this.toAccount = '';
    this.amount = null;
  }

  cancel() {

  }

  ngOnInit(): void {
  }

}
